import {
  Container,
  Row,
  Col,
  Image,
  Card,
  Button,
  Table,
} from "react-bootstrap";
import profile from "../img/profile.jpg";
import { Outlet, useNavigate } from "react-router-dom";
import { useState } from "react";

import "../components/Button.css";
const ProfilePage = (props) => {
  const [name, setName] = useState(localStorage.getItem("name"));
  const [email, setEmail] = useState(props.currentUser);
  const [pass, setPass] = useState(localStorage.getItem("password"));

  const navigate = useNavigate();

  if (props.loginStatus !== true) {
    navigate("/");
  }

  return (
    <Container>
      <Row className="px-4 my-5">
        {/* PAGE LEFT:  */}
        <Col sm={7}>
          {/* Dummy placeg=holder for now- TODO replace with company logo */}
          <Image src={profile} fluid rounded className="" width="300" />
          {/* image generated from https://smashinglogo.com/ and used for non for profit educational purposes */}
        </Col>

        {/* PAGE RIGHT */}
        <Col sm={5}>
          <Card>
            <Card.Header>Profile</Card.Header>
            <Card.Body>
              <Card.Text>
                <Table striped bordered hover size="sm">
                  <thead>
                    <tr>
                      <th>Name</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{name}</td>
                    </tr>
                  </tbody>
                </Table>
                <Table striped bordered hover size="sm">
                  <thead>
                    <tr>
                      <th>Email</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{email}</td>
                    </tr>
                  </tbody>
                </Table>
              </Card.Text>
              <Button variant="primary" className="button-bigger">
                Edit profile
              </Button>
              <Button variant="secondary" className="button-bigger">
                Change Password
              </Button>
            </Card.Body>
          </Card>

          {}
          <Outlet />
        </Col>
      </Row>
      <Row></Row>
    </Container>
  );
};

export default ProfilePage;
