import { Card, Button, Table } from "react-bootstrap";
import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { getUser, findUser } from "../data/repository";
import "../Assets/CSS/Profile.css";

const Profile = async (props) => {
  const userLocalStorage = getUser();

  const accessUser = async () => {
    const result = await findUser(userLocalStorage.email);
    return result;
  };

  let user = await accessUser();

  console.log(user.email);

  const navigate = useNavigate();

  useEffect(() => {
    if (getUser() === null) {
      navigate("/");
    }
  });

  return (
    <Card>
      <Card.Header>Profile</Card.Header>
      <Card.Subtitle className="mb-2 text-muted padded-date">
        join date: {userLocalStorage.joinDate}
      </Card.Subtitle>
      <Card.Body>
        <Card.Text>
          <Table striped bordered hover size="sm">
            <thead>
              <tr>
                <th>Name</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{user.name}</td>
              </tr>
            </tbody>
          </Table>
          <Table striped bordered hover size="sm">
            <thead>
              <tr>
                <th>Email</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{user.email}</td>
              </tr>
            </tbody>
          </Table>
        </Card.Text>

        <Link to="/profile/edit">
          <Button variant="primary" className="button-bigger more-left-margin">
            Edit profile
          </Button>
        </Link>
        <Link to="/profile/changePassword">
          <Button
            variant="secondary"
            className="button-bigger more-left-margin"
          >
            Change Password
          </Button>
        </Link>
        <Link to="/profile/delete">
          <Button variant="danger" className="button-bigger more-left-margin">
            Delete Account
          </Button>
        </Link>
      </Card.Body>
    </Card>
  );
};

export default Profile;
