import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { BrowserRouter, Route, Routes, useNavigate} from "react-router-dom";
import NavBarComponent from "./components/NavBarComponent";
import { Container } from "react-bootstrap";
import LandingPage from "./pages/LandingPage";
import { useState } from "react";

import LoginForm from "./components/LoginForm";
import LoginMenu from "./components/LoginMenu";
import RegisterForm from "./components/RegisterForm";
import ProfilePage from "./pages/ProfilePage";

function App() {
  const [login, setLogin] = useState(false);
  const [currentUser, setCurrentUser] = useState("")


    const logInUser = (user) =>{

      setCurrentUser(user);
      console.log("setCurrentUser" + user);

    };

    console.log("APPUSER: " +currentUser);

  return (
    <React.StrictMode>
    <BrowserRouter>
      <div className="App">
        <header>
          <NavBarComponent loggedIn={login}></NavBarComponent>
        </header>

        <main>
          <Routes>
            <Route path="/" element={<LandingPage />}>
            <Route path="/" element={<LoginMenu loginStatus= {login}/>} />
              <Route path="/loginForm" element={<LoginForm logInUser={logInUser} setLogin = {setLogin}/>} />
              <Route path="/register" element={<RegisterForm />} />

            </Route>
            <Route path="/profile" element={<ProfilePage loginStatus= {login} curentUser = {currentUser}/>} />

          </Routes>
        </main>

        <footer className="py-5 my5 bg-dark">
          <Container className="px-4">
            <p className="text-white text-center">
              Copyright &copy; Matthew Bird/RMIT 2022
            </p>
          </Container>
        </footer>
      </div>
    </BrowserRouter>
    </React.StrictMode>
  );
}

export default App;
