import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { BrowserRouter, Route, Routes, useNavigate} from "react-router-dom";
import NavBarComponent from "./components/NavBarComponent";
import { Container } from "react-bootstrap";
import LandingPage from "./pages/LandingPage";
import { useState } from "react";

import LoginForm from "./components/LoginForm";
import LoginMenu from "./components/LoginMenu";
import RegisterForm from "./components/RegisterForm";

function App() {
  const [login, setLogin] = useState(false);
  const navigate = useNavigate();

  const loginUser = (email,password) => {
    let storedEmail = localStorage.getItem("email")
    let storedPassword = localStorage.getItem("password")

    if(storedEmail === email && storedPassword === password){
      setLogin(true);
      navigate("/")



    }


  }

  const registerUser = (name, email, password)=>{

    localStorage.setItem("name", name);
    localStorage.setItem("email", email);
    localStorage.setItem("password", password);

  }

  return (
    <BrowserRouter>
      <div className="App">
        <header>
          <NavBarComponent loggedIn={login}></NavBarComponent>
        </header>

        <main>
          <Routes>
            <Route path="/" element={<LandingPage />}>
            <Route path="/" element={<LoginMenu loginStatus= {login}/>} />
              <Route path="/loginForm" element={<LoginForm loginUser= {loginUser}/>} />
              <Route path="/register" element={<RegisterForm registerUser= {registerUser}/>} />

            </Route>

          </Routes>
        </main>

        <footer className="py-5 my5 bg-dark">
          <Container className="px-4">
            <p className="text-white text-center">
              Copyright &copy; Matthew Bird/RMIT 2022
            </p>
          </Container>
        </footer>
      </div>
    </BrowserRouter>
  );
}

export default App;
