import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { BrowserRouter, Route, Routes} from "react-router-dom";
import NavBarComponent from "./components/NavBarComponent";
import { Container } from "react-bootstrap";
import { UserContext } from "./components/UserContext";
import { useState } from "react";


import LoginForm from "./components/LoginForm";
import LoginMenu from "./components/LoginMenu";
import RegisterForm from "./components/RegisterForm";
import LandingPage from "./pages/LandingPage";
import Logout from "./components/Logout";


function App() {
  const [login, setLogin] = useState(false);
  const [user, setUser] = useState("");


  return (
    <UserContext.Provider value= {{user,setUser}}>
    <BrowserRouter>
      <div className="App">
        <header>
          <NavBarComponent loggedIn={login}></NavBarComponent>
        </header>

        <main>

          <Routes>

            <Route path="/" element={<LandingPage />}>

            <Route path="/" element={<LoginMenu loginStatus= {login}/>} />
              <Route path="/loginForm" element={<LoginForm setLogin = {setLogin}/>} />
              <Route path="/register" element={<RegisterForm />} />

            </Route>
            <Route path="/logout" element={<Logout />}/>


          </Routes> 
        </main>

        <footer className="py-5 my5 bg-dark">
          <Container className="px-4">
            <p className="text-white text-center">
              Copyright &copy; Matthew Bird/RMIT 2022
            </p>
          </Container>
        </footer>
      </div>
    </BrowserRouter>
    </UserContext.Provider>
  );
}

export default App;
