import { useState } from "react";
import { Button, Row, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import "../Assets/CSS/Button.css";
import { getUser } from "../data/repository";
import "../Assets/CSS/Login.css";

const Login = (props) => {
  return (
    <div className="form">
      {props.loginStatus !== true && (
        <div>
          <h1 className="sign-in-heading">sign in to continue</h1>
          <p className="mt-4">
            {" "}
            company policy requires that all content is to be only accessed by
            registered members!
          </p>
          <Row>
            <Col sm={6}>
              <Link to="/loginForm">
                <Button className="mx-1 my-2 button-bigger"> Login</Button>
              </Link>
            </Col>
            <Col sm={6}>
              <Link to="/register">
                {" "}
                <Button className="mx-1 my-2 button-bigger" variant="secondary">
                  Register
                </Button>
              </Link>
            </Col>
          </Row>
        </div>
      )}
      {props.loginStatus === true && (
        <div>
          <h2>Welcome...</h2>
          <h3>{getUser().email}</h3>
        </div>
      )}
    </div>
  );
};

export default Login;
