import "../Assets/CSS/Reaction.css";
import { useState } from "react";

const Reaction = () => {
  const happy = "ðŸ˜";
  const sad = "ðŸ˜ž";
  const angry = "ðŸ˜ ";
  const like = "ðŸ‘";
  const dislike = "ðŸ‘Ž";

  const reactionIds = ["like", "dislike", "happy", "sad", "angry"];
  const [userReaction, setUserReaction] = useState("");

  const handleReaction = (reaction) => {
    setUserReaction(reaction);
    displayUserReaction(reaction);
  };

  const displayUserReaction = (reaction) => {
    for (const id of reactionIds) {
      const icon = document.getElementById(reaction);
      icon.style.backgroundColor = "white";
    }
    const icon = document.getElementById(reaction);
    icon.style.backgroundColor = "blue";
  };
  return (
    <div>
      {userReaction}
      <table>
        <tr>
          <th onClick={(e) => handleReaction("like")}>
            <button id="like" className="reaction">
              {like}
            </button>
          </th>{" "}
          <th onClick={(e) => handleReaction("dislike")}>
            <button id="dislike" className="reaction">
              {dislike}
            </button>
          </th>
          <th onClick={(e) => handleReaction("happy")}>
            <button id="happy" className="reaction">
              {happy}
            </button>
          </th>
          <th onClick={(e) => handleReaction("sad")}>
            <button id="sad" className="reaction">
              {sad}
            </button>
          </th>{" "}
          <th onClick={(e) => handleReaction("angry")}>
            <button id="angry" className="reaction">
              {angry}
            </button>
          </th>{" "}
        </tr>
        <tr className="reaction-count">
          <td>1</td>
          <td></td>
          <td>2</td>
          <td>1</td>
          <td>1</td>
        </tr>
      </table>
    </div>
  );
};

export default Reaction;
