  };

  const [userReaction, setUserReaction] = useState("");
  return (
    <div>
      <table>
        <tr>
          <th onClick={() => handleReaction("like")}>
            <button className="reaction">{like}</button>
          </th>{" "}
          <th onClick={() => handleReaction("dislike")}>
